import React, { useState, useEffect } from "react";
import Title from "../../Components/Title/Title";
import TableEv from "./TableEv/TableEv";
import "./EventosPage.css";
import MainContent from "../../Components/MainContent/MainContent";
import ImageIllustrator from "../../Components/ImageIllustrator/ImageIllustrator";
import Notification from "../../Components/Notification/Notification.js";
import eventImage from "../../assets/images/evento.svg";
import Container from "../../Components/Container/Container";
import {
  Button,
  Input,
  Select,
} from "../../Components/FormComponents/FormComponents";
import api from "../../Services/Service";

const EventoPage = () => {
  const [titleEvento, setTitulo] = useState("");
  const [description, setDescription] = useState("");
  const [tipoEventos, setTipoEventos] = useState([]);
  const [idTipoEvento, setIdTipoEvento] = useState("");
  const [idInstituicao, setIdInstituicao] = useState("");
  const [instituicao, setInstituicao] = useState([]);
  const [data, setData] = useState("");

  const [frmEdit, setFrmEdit] = useState(false);
  const [eventos, setEventos] = useState([]);
  const [notifyUser, setNotifyUser] = useState({});
  const [idEvento, setIdEvento] = useState(null);
  const [showSpinner, setShowSpinner] = useState(false);

  async function getEventos() {
    try {
      setShowSpinner(true);
      const retorno = await api.get(`/Evento`);
      setEventos(retorno.data);
    } catch (error) {
      console.log("moio");
    }
    setShowSpinner(false);
  }

  async function getInstituicao() {
    try {
      const retorno = await api.get(`/Instituicao`);
      setInstituicao(retorno.data);
    } catch (error) {
      console.log("moio");
    }
  }

  async function getTipoEventos() {
    try {
      const rt = await api.get(`/TiposEvento`);
      setTipoEventos(rt.data);
    } catch (error) {
      alert("deu ruim");
    }
  }

  //sempre que renderizar page, vai chamar as func
  useEffect(() => {
    getEventos();
    getInstituicao();
    getTipoEventos();
  }, []);

  async function handlePost(e) {
    //parar o submit do form
    e.preventDefault();

    //chamar api
    try {
      const retorno = await api.post("/Evento", {
        nomeEvento: titleEvento,
        descricao: description,
        idTipoEvento: idTipoEvento,
        idInstituicao: idInstituicao,
        dataEvento: data,
      });

      setNotifyUser({
        titleNote: "Sucesso",
        textNote: `Cadastrado com sucesso!`,
        imgIcon: "success",
        imgAlt:
          "Imagem de ilustração de sucesso. Moça segurando um balão com símbolo de confirmação ok.",
        showMessage: true,
      });

      getEventos();
      setDescription("");
      setTitulo("");
      setData("");
    } catch (error) {
      console.log("Deu ruim na API");
      console.log(error);
    }

    setTitulo(""); //limpa a variavel
    setDescription("");
    setData("");
  }

  async function handleUpdate(e) {
    e.preventDefault();
    try {
      const retorno = await api.put(`/Evento/` + idEvento, {
        nomeEvento: titleEvento,
        descricao: description,
        idTipoEvento: idTipoEvento,
        idInstituicao: idInstituicao,
        dataEvento: data,
      });

      setNotifyUser({
        titleNote: "Sucesso",
        textNote: `Atualizado com sucesso!`,
        imgIcon: "success",
        imgAlt:
          "Imagem de ilustração de sucesso. Moça segurando um balão com símbolo de confirmação ok.",
        showMessage: true,
      });

      setTitulo(""); //limpa a variavel
      setDescription("");
      setData("");
      setIdInstituicao("");
      setIdTipoEvento("");
      setFrmEdit(false);

      //atualizar o state (api get)
      const retornoGet = await api.get(`/Evento`);
      setEventos(retornoGet.data); //atualiza o state da tabela
      editActionAbort(); //limpa o state do titulo e id
    } catch (error) {
      alert("Deu ruim no update");
    }
  }

  function editActionAbort() {
    setFrmEdit(false);
    setData("");
    setTitulo("");
    setDescription("");
    setIdInstituicao("");
    setIdTipoEvento("");
  }

  async function mostraUpdate(e) {
    setFrmEdit(true);
    setData(e.dataEvento.slice(0, 10));
    setTitulo(e.titulo);
    setDescription(e.descricao);
    setIdInstituicao(e.idInstituicao);
    setIdTipoEvento(e.idTipoEvento);
    setIdEvento(e.idEvento);
  }
  async function handlerDelete(id) {
    try {
      const retorno = await api.delete(`/Evento/${id}`);
      // const promise = await api.get(`/TiposEvento`);
      getEventos();
    } catch (error) {
      console.log("não apagou");
    }
  }

  return (
    <MainContent>
      <Notification {...notifyUser} setNotifyUser={setNotifyUser} />
      {showSpinner ? <Spinner /> : null}
      <Container>
        <section className="cadastro-evento-section">
          <Container>
            <div className="cadastro-evento__box">
              <Title titleText={"Página de Eventos"} className="margem-acima" />
              <ImageIllustrator alterText={""} imageRender={EventTypeImage} />

              <form
                onSubmit={frmEdit ? handleUpdate : handlePost}
                className="ftipo-evento"
              >
                {/*Exclamação '!' indica negação, se não estiver em tela de edição*/}
                {!frmEdit ? (
                  /*Mostra tela de Cadastrar*/
                  <>
                    <Input
                      id={"evento"}
                      name={"evento"}
                      placeholder={"Evento"}
                      type={"text"}
                      required="required"
                      value={titleEvento}
                      manipulationFunction={(e) => {
                        setTitulo(e.target.value);
                      }}
                    />
                    <Input
                      id={"descricao"}
                      name={"descricao"}
                      placeholder={"Descrição"}
                      type={"text"}
                      required="required"
                      value={description}
                      manipulationFunction={(e) => {
                        setDescription(e.target.value);
                      }}
                    />

                    <SelectTipoEventos
                      dados={tipoEventos}
                      id="idTipoEvento"
                      placeholder="Tipo de Eventos"
                      name="idTipoEvento"
                      value={idTipoEvento}
                      manipulationFunction={(e) => {
                        setIdTipoEvento(e.target.value);
                      }}
                    />

                    <SelectInstituicao
                      dados={instituicao}
                      id="idInstituicao"
                      placeholder="Instituição"
                      name="idInstituicao"
                      value={idInstituicao}
                      manipulationFunction={(e) => {
                        setIdInstituicao(e.target.value);
                      }}
                    />

                    <Input
                      id={"data"}
                      name={"data"}
                      placeholder={"dd/mm/aaaa"}
                      type={"date"}
                      required="required"
                      value={data}
                      manipulationFunction={(e) => {
                        setData(e.target.value);
                      }}
                    />
                    {/* <span>{titulo}</span> */}

                    <Button
                      id={"cadastrar"}
                      name={"cadastrar"}
                      type={"submit"}
                      textButton={"Cadastrar"}
                      additionalClass={"button-component"}
                      manipulationFunction={handlePost}
                    />
                  </>
                ) : (
                  <>
                    {/* tela de edição */}
                    <Input
                      id={"evento"}
                      name={"evento"}
                      placeholder={"Evento"}
                      type={"text"}
                      required="required"
                      value={titleEvento}
                      manipulationFunction={(e) => {
                        setTitulo(e.target.value);
                      }}
                    />
                    <Input
                      id={"descricao"}
                      name={"descricao"}
                      placeholder={"Descrição"}
                      type={"text"}
                      required="required"
                      value={description}
                      manipulationFunction={(e) => {
                        setDescription(e.target.value);
                      }}
                    />

                    <SelectTipoEventos
                      dados={tipoEventos}
                      id="idTipoEvento"
                      placeholder="Tipo de Eventos"
                      name="idTipoEvento"
                      value={idTipoEvento}
                      manipulationFunction={(e) => {
                        setIdTipoEvento(e.target.value);
                      }}
                    />

                    <SelectInstituicao
                      dados={instituicao}
                      id="idInstituicao"
                      placeholder="Instituição"
                      name="idInstituicao"
                      value={idInstituicao}
                      manipulationFunction={(e) => {
                        setIdInstituicao(e.target.value);
                      }}
                    />

                    <Input
                      id={"data"}
                      name={"data"}
                      placeholder={"dd/mm/aaaa"}
                      type={"date"}
                      required="required"
                      value={data}
                      manipulationFunction={(e) => {
                        setData(e.target.value);
                      }}
                    />

                    <div className="buttons-editbox">
                      <Button
                        id={"atualizar"}
                        name={"atualizar"}
                        type={"submit"}
                        textButton={"Atualizar"}
                        additionalClass={"button-component--middle"}
                      />

                      <Button
                        id={"cancelar"}
                        name={"cancelar"}
                        type={"reset"}
                        textButton={"Cancelar"}
                        additionalClass={"button-component--middle"}
                        manipulationFunction={editActionAbort}
                      />
                    </div>
                  </>
                )}
              </form>
            </div>
          </Container>
        </section>
      </Container>

      <section className="lista-eventos-section">
        <Container>
          <Title titleText={"Lista de eventos"} color="white" />
          <TableEp
            dados={eventos}
            fnUpdate={mostraUpdate}
            fnDelete={handlerDelete}
          />
        </Container>
      </section>
    </MainContent>
  );
};

export default EventoPage;
