import React from "react";
import "./FormComponents.css";

export const Input = ({
  type,
  id,
  required,
  additionalClass = "",
  name,
  placeholder,
  manipulationFunction,
  value,
}) => {
  return (
    <input
      type={type}
      id={id}
      name={name}
      value={value}
      required={required}
      className={`input-component ${additionalClass}`}
      placeholder={placeholder}
      onChange={manipulationFunction}
      autoComplete="off"
    />
  );
};

export const Button = ({
  textButton,
  id,
  name,
  type,
  additionalClass = "",
  manipulationFunction,
}) => {
  return (
    <button
      type={type}
      name={name}
      id={id}
      className={`button-component ${additionalClass}`}
      onClick={manipulationFunction}
    >
      {textButton}
    </button>
  );
};

const options = [
    { value: "23123123", text: "vinicius" },
    { value: "3333", text: "sadadsda" }
  ];
  
  //SELECT
  export const Select = ({
      dados = [],
      id,
      name,
      required, 
      additionalClass = "",
      manipulationFunction,
      defaultValue
  }) => {
      return (
      <select
        name={name}
        required={required}
        id={id}
        className={`input-component ${additionalClass}`}
        onChange={manipulationFunction}
        value={defaultValue}
      >
        <option value="">Selecione uma opção</option>
        {dados.map((opt) => {
          return <option value={opt.value}>{opt.text}</option>;
        })}
      </select>
    );
  };
